set.commands:
PACKAGE_NAME=dmalloc PACKAGE_VERSION=5.5.2
oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.tar.gz
oeg_include_files README
configure --prefix=${OEG_BASE_DIR} --with-sysroot=${OEG_BASE_DIR} --with-gnu-ld --disable-rpath --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} --enable-shlib --enable-threads make make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oeg_fix_path_names #oeg_fix_libtool_file libidn.la libidn-11.dll libidn.dll.a libidn.a oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
