set.commands:
PACKAGE_NAME=freetype PACKAGE_VERSION=2.5.2
oegimport bzip2 oegimport zlib oegimport libpng
oegextract freetype
mkdir -p "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}" cp README docs/GPL* docs/LICENSE.TXT "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}/"
# configure failed in 2.5.2 (.tar.bz2). Diffed against the zip archive: # diff -riEBbw --strip-trailing-cr freetype-2.5.2-tbz freetype-2.5.2-zip # many PaxHeaders.20920 directories, we have to remove them all:
find . -name "PaxHeaders.20920" -exec rm -rf {} \;
configure --prefix=${OEG_BASE_DIR} --with-sysroot=${OEG_BASE_DIR} --with-gnu-ld --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} #--disable-mmap
make make install DESTDIR=${OEG_INSTALL_DIR}
cd ${OEG_INSTALL_DIR}/${OEG_BASE_DIR}
mv bin/*.dll bin64/ rm -rf bin # TODO: Do we need to edit the .la files in lib64?
oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
