set.commands:
PACKAGE_NAME=gcc PACKAGE_VERSION=4.8.2
oegextract ${PACKAGE_NAME}
oeg_include_files COPYING* MAINTAINERS README
# ftp://gcc.gnu.org/pub/gcc/infrastructure/. # http://gcc.gnu.org/install/prerequisites.html
oegimport gmp # required: 4.2+ oegimport mpfr # required: 2.4.0+ oegimport mpc # required: 0.8.0+ oegimport libiconv # optional oegimport gettext # needed? only added because of --enable-nls; TODO: Check
mkdir build cd build ../configure --prefix=${OEG_BASE_DIR} --with-sysroot=${OEG_BASE_DIR} --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA}
make make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
