set.commands:
PACKAGE_NAME=gnuit PACKAGE_VERSION=4.9.5
oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.tar.gz oegimport ncurses # or termcap, curses, ncursesw, termlib, ...
configure --prefix=${PREFIX} --enable-gcc-warnings make make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
