set.commands:
PACKAGE_NAME=help2man PACKAGE_VERSION=1.43.3
#requires perl!
oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.tar.gz
oeg_include_files COPYING README THANKS
configure --prefix=${OEG_BASE_DIR} --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} make #no strip make install DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
