set.commands:
PACKAGE_NAME=jasper PACKAGE_VERSION=1.900.1
oegimport libglut # optional #oegimport libiconv #oegimport gettext
oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.zip
mkdir -p "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}" #cp XXX "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}/"
# --with-x use the X Window System # --with-glut-include-dir=DIR Set GLUT include directory. # --with-glut-lib-dir=DIR Set GLUT library directory. configure --prefix=${OEG_BASE_DIR} --without-x --with-sysroot=${OEG_BASE_DIR} --with-gnu-ld --disable-rpath --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} make make install DESTDIR=${OEG_INSTALL_DIR}
cd ${OEG_INSTALL_DIR}/${OEG_BASE_DIR}
mv bin/*.dll bin64/ rm -rf bin # TODO: Do we need to edit the .la files in lib64?
oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
