set.commands:
PACKAGE_NAME=liboop PACKAGE_VERSION=1.0
#oegimport glib #oegimport adns
oegextract liboop.tar.gz cd liboop-1.0
mkdir -p "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}" cp COPYING "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}/"
configure --prefix=${OEG_BASE_DIR} --with-gnu-ld --without-tcl --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} make make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd ${OEG_INSTALL_DIR}/${OEG_BASE_DIR}
mv bin/*.dll bin64/ rm -rf bin # TODO: Do we need to edit the .la files in lib64?
oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
