set.commands:
PACKAGE_NAME=lzop PACKAGE_VERSION=1.03
oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.tar.gz
oeg_include_files AUTHORS COPYING THANKS
oegimport lzo
configure --prefix=${OEG_BASE_DIR} --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} make make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oeg_fix_path_names oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
