set.commands:
PACKAGE_NAME=mpc PACKAGE_VERSION=1.0.2
oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.tar.gz
oeg_include_files AUTHORS COPYING* README
oegimport gmp # required: 4.3.2+ oegimport mpfr # required: 2.4.2+
configure --prefix=${OEG_BASE_DIR} --with-sysroot=${OEG_BASE_DIR} --with-gnu-ld --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} --disable-static --enable-shared make make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oeg_fix_path_names oeg_fix_libtool_file libmpc.la libmpc-3.dll libmpc.dll.a "" oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
