set.commands:
PACKAGE_NAME=mpfr PACKAGE_VERSION=3.1.2
oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.tar.xz
oeg_include_files AUTHORS COPYING* REAME
oegimport gmp
configure --prefix=${OEG_BASE_DIR} --with-sysroot=${OEG_BASE_DIR} --with-gnu-ld --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} --disable-static --enable-shared # static build requires static gmp and it's include with the static define. make make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oeg_fix_path_names oeg_fix_libtool_file libmpfr.la libmpfr-4.dll libmpfr.dll.a "" oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
