set.commands:
PACKAGE_NAME=opus-tools PACKAGE_VERSION=0.1.8
oegimport opus # optional? oegimport libogg # optional oegimport flac # optional
oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.tar.gz
mkdir -p "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}" #cp AUTHORS COPYING README "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}/"
configure --prefix=${OEG_BASE_DIR}
#--with-ogg=${OEG_BASE_DIR} --with-ogg-libraries=${OEG_BASE_LIB_DIR} --with-ogg-includes=${OEG_BASE_DIR}/include #--with-opus=${OEG_BASE_DIR} --with-opus-libraries=${OEG_BASE_LIB_DIR} --with-opus-includes=${OEG_BASE_DIR}/include #--without-flac disable FLAC support
make make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}"
mv bin bin64 # TODO: Do we need to edit the .la files in lib64?
oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
