set.commands:
PACKAGE_NAME=poldi PACKAGE_VERSION=0.4.1
oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.tar.bz2
oeg_include_files AUTHORS COPYING README THANKS
oegimport libiconv oegimport gettext oegimport libksba oegimport libgcrypt oegimport libgpg-error
configure --prefix=${OEG_BASE_DIR} --with-sysroot=${OEG_BASE_DIR} --with-gnu-ld --disable-rpath --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} make make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oeg_fix_path_names # oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
