set.commands:
PACKAGE_NAME=rtmpdump PACKAGE_VERSION=2.3
oegimport openssl oegimport zlib
oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.tar.gz
mkdir -p "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}" cp librtmp/COPYING COPYING.librtmp cp COPYING* README "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}/"
XLDFLAGS= SYS=mingw
LDFLAGS="-L${OEG_BASE_LIB_DIR}" make SYS=mingw CRYPTO=OPENSSL prefix=/open-egovernment-install${OEG_BASE_DIR} libdir=${OEG_BASE_LIB_DIR} bindir=${OEG_BASE_BIN_DIR}
configure --prefix=${OEG_BASE_DIR} --with-sysroot=${OEG_BASE_DIR} --with-gnu-ld --disable-rpath --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} make make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}"
mv bin/*.dll bin64/ rm -rf bin # TODO: Do we need to edit the .la files in lib64?
oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
