set.commands:
PACKAGE_NAME=trousers PACKAGE_VERSION=0.3.11.2
oegimport openssl oegimport zlib oegimport gmp oegimport readline
oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.tar.gz
mkdir -p "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}" cp AUTHORS LICENSE README "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}/"
oegpatch 001 # configure: if --with-openssl given it searched for */lib instead of */lib64
configure --prefix=${OEG_BASE_DIR} --with-sysroot=${OEG_BASE_DIR} --with-gui=none --with-gnu-ld --enable-shared --with-gmp --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} --with-openssl=${OEG_BASE_DIR}
oegpatch 002 # src/include/spi_utils.h oegpatch 003 # src/include/tcslog.h oegpatch 004 # src/include/tddl.h oegpatch 005 # src/include/trousers_types.h oegpatch 006 # src/include/tsplog.h oegpatch 007 # src/tddl/tddl.c oegpatch 008 # src/trspi/trousers.c oegpatch 009 # src/include/tcsd.h oegpatch 010 # src/include/tcslog.h oegpatch 011 # src/tcs/log.c oegpatch 012 # src/tcs/tcs_caps.c
make make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
