set.commands:
PACKAGE_NAME=bzip2 PACKAGE_VERSION=1.0.6
oegextract bzip2
oeg_include_files LICENSE README
oegpatch 001 # patch Makefile to ignore chmod with missing extensions. make make install PREFIX=${OEG_INSTALL_DIR}${OEG_BASE_DIR} make clean
oegpatch 002 # patch Makefile to create a DLL instead of a SO library. make -f Makefile-libbz2_so make -f Makefile-libbz2_so install PREFIX=${OEG_INSTALL_DIR}${OEG_BASE_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oeg_fix_path_names oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
