set.commands:
PACKAGE_NAME=gettext PACKAGE_VERSION=0.18.3.2
oegextract ${PACKAGE_NAME}
oeg_include_files AUTHORS COPYING THANKS
oegimport libiconv
configure --prefix=${OEG_BASE_DIR} --with-gnu-ld --enable-relocatable --disable-rpath --with-libiconv-prefix=${OEG_BASE_DIR} --with-sysroot=${OEG_BASE_DIR} --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} --without-emacs make cp gettext-runtime/intl/libintl.la ${OEG_INSTALL_DIR}/${OEG_BASE_LIB_DIR}/ make install DESTDIR=${OEG_INSTALL_DIR}
# TODO: package could be splitted into gettext-tools and gettext-runtime.
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oeg_fix_path_names oeg_fix_libtool_file libasprintf.la libasprintf-0.dll libasprintf.dll.a libasprintf.a oeg_fix_libtool_file libgettextlib.la libgettextlib-0-18-3.dll libgettextlib.dll.a libgettextlib.a oeg_fix_libtool_file libgettextpo.la libgettextpo-0.dll libgettextpo.dll.a libgettextpo.a oeg_fix_libtool_file libgettextsrc.la libgettextsrc-0-18-3.dll libgettextsrc.dll.a libgettextsrc.a oeg_fix_libtool_file libintl.la libintl-8.dll libintl.dll.a libintl.a oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
