set.commands:
PACKAGE_NAME=icu PACKAGE_VERSION=52.1
oegextract icu4c # icu4c-52_1-src.zip, not tgz! cd icu
mkdir -p "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}" #cp AUTHORS COPYING.LIB README THANKS "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}/"
cd source
configure --prefix=${OEG_BASE_DIR} --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA}
make # there is no install-strip! make install DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}"
mv lib64/*.dll bin64/ # TODO: Do we need to edit the .la files in lib64? # TODO: link files!
oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
