set.commands:
PACKAGE_NAME=json-c PACKAGE_VERSION=0.11
oegextract ${PACKAGE_NAME}
oeg_include_files AUTHORS COPYING README
configure --prefix=${OEG_BASE_DIR} --with-gnu-ld --enable-shared --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} make #make check
oegpatch 001 # Makefile fails, installing includes, fixed.
make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oeg_fix_path_names oeg_fix_libtool_file libjson.la libjson-0.dll libjson.dll.a libjson.a oeg_fix_libtool_file libjson-c.la libjson-c-2.dll libjson-c.dll.a libjson-c.a oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
