set.commands:
oegenv 32 oegcleanbase oegextract libffi-3.0.13.tar.gz configure --prefix=${OEG_BASE_DIR} --with-gnu-ld --with-sysroot=${OEG_BASE_DIR} --enable-shared make make install-strip DESTDIR=${OEG_INSTALL_DIR}
# error: includes are in lib/libffi-3.0.13/include/*.h mv ${OEG_INSTALL_DIR}${OEG_BASE_DIR}/lib/libffi-3.0.13/include ${OEG_INSTALL_DIR}${OEG_BASE_DIR}/ rm -rf ${OEG_INSTALL_DIR}${OEG_BASE_DIR}/lib/libffi-3.0.13
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oegarchive libffi-3.0.13
