set.commands:
PACKAGE_NAME=libiconv PACKAGE_VERSION=1.14
oegextract libiconv
oeg_include_files AUTHORS COPYING* THANKS
oegimport libiconv # For gettext binaries, called during the libiconv build. oegimport gettext
# --enable-relocatable doesn't work in 1.14, just remove this parameter: #../srclib/libicrt.a(progreloc.o):progreloc.c:(.text+0x156): undefined reference to `compute_curr_prefix' #../srclib/libicrt.a(progreloc.o):progreloc.c:(.text+0x168): undefined reference to `set_relocation_prefix' # reason is a missing relocatable.o in srclib/libicrt.a # http://savannah.gnu.org/bugs/?38422 # --enable-extra-encodings - add support for a few rarely used encodings - do we want this?
configure --prefix=${OEG_BASE_DIR} --with-sysroot=${OEG_BASE_DIR} --enable-relocatable --enable-shared --with-gnu-ld --disable-rpath --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} oegpatch 001 # Add missing relocatable.o to DLL. make make install-strip DESTDIR=${OEG_INSTALL_DIR}
make distclean
configure --prefix=${OEG_BASE_DIR} --with-sysroot=${OEG_BASE_DIR} --enable-relocatable --disable-shared --enable-static --with-gnu-ld --disable-rpath --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} oegpatch 001 # Add missing relocatable.o to DLL. make make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oeg_fix_path_names oeg_fix_libtool_file libiconv.la libiconv-2.dll libiconv.dll.a libiconv.a oeg_fix_libtool_file libcharset.la libcharset-1.dll libcharset.dll.a libcharset.a oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
