set.commands:
PACKAGE_NAME=libogg PACKAGE_VERSION=1.3.1
oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.tar.xz
oeg_include_files AUTHORS COPYING README
configure --prefix=${OEG_BASE_DIR} --with-gnu-ld --with-sysroot=${OEG_BASE_DIR} --enable-shared --enable-static --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} make make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oeg_fix_path_names oeg_fix_libtool_file libogg.la libogg-0.dll libogg.dll.a libogg.a oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
