set.commands:
PACKAGE_NAME=libtasn1 PACKAGE_VERSION=3.4
oegextract libtasn1 configure --prefix=${OEG_BASE_DIR} --with-sysroot=${OEG_BASE_DIR} --with-gnu-ld --disable-gtk-doc --disable-gtk-doc-pdf --disable-gtk-doc-html --enable-shared --enable-static --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA}
oegpatch 001
make make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}"
mv bin/*.dll bin64/ rm -rf bin # TODO: Do we need to edit the .la files in lib64?
oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
