set.commands:
PACKAGE_NAME=libunistring PACKAGE_VERSION=0.9.3
oegimport libiconv oegextract libunistring
configure --with-gnu-ld -with-libiconv-prefix=${OEG_BASE_DIR} --without-libpth-prefix --prefix=${OEG_BASE_DIR} --enable-relocatable --enable-threads=win32 --disable-rpath --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} make make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}"
mv bin/*.dll bin64/ rm -rf bin # TODO: Do we need to edit the .la files in lib64?
oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
