cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oeg_fix_path_names oeg_fix_libtool_file liblzo2.la "" "" liblzo2.a oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
