set.commands:
PACKAGE_NAME=opus PACKAGE_VERSION=1.1
oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.tar.gz
oeg_include_files AUTHORS COPYING README
configure --prefix=${OEG_BASE_DIR} --with-sysroot=${OEG_BASE_DIR} --with-gnu-ld --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} make make install-strip DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oeg_fix_path_names oeg_fix_libtool_file libopus.la libopus-0.dll libopus.dll.a libopus.a oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
