set.commands:
PACKAGE_NAME=peflags PACKAGE_VERSION=2.4.5
mkdir ${PACKAGE_NAME}-${PACKAGE_VERSION} cd ${PACKAGE_NAME}-${PACKAGE_VERSION}
cp /i/Projekte/open-egov/data/builder/sources/peflags.c .
gcc ${CFLAGS} ${LDFLAGS} -o peflags.exe -DVERSION='"2.4.5"' peflags.c
strip -s peflags.exe mkdir -p "${OEG_INSTALL_DIR}${OEG_BASE_BIN_DIR}" cp peflags.exe "${OEG_INSTALL_DIR}${OEG_BASE_BIN_DIR}"
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
