set.commands:
PACKAGE_NAME=portaudio
PACKAGE_VERSION=svn-DATE

oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.tgz

make install DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}"
oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}

PACKAGE_NAME=portaudio PACKAGE_VERSION=19-20140121
oegextract pa_stable_v19_20140121_rc.tar.gz cd portaudio
oeg_include_files LICENSE.txt README.txt
configure --prefix=${OEG_BASE_DIR} --with-gnu-ld --with-sysroot=${OEG_BASE_DIR} --disable-cxx --enable-shared --enable-static --disable-debug-output make make tests make install DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oeg_fix_path_names oeg_fix_libtool_file libportaudio.la libportaudio-2.dll libportaudio.dll.a libportaudio.a oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
