set.commands:
PACKAGE_NAME=qpdf PACKAGE_VERSION=5.1.1
oegimport zlib oegimport pcre
oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.tar.gz
mkdir -p "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}" cp README Artistic-2.0 "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}/"
configure --prefix=${OEG_BASE_DIR} --with-sysroot=${OEG_BASE_DIR} --with-gnu-ld --bindir=${OEG_BASE_BIN_DIR} --libdir=${OEG_BASE_LIB_DIR} ${CONFIGURE_HOST_PARA} --enable-shared --with-windows-wordsize=${TARGETBITS}
oegpatch 001 # extension missing while installing; version 5.0.0, 5.1.1
make #strip here #no shared dll make install DESTDIR=${OEG_INSTALL_DIR}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
