set.commands:
PACKAGE_NAME=rtmpdump PACKAGE_VERSION=2.3
oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.tar.gz
cp librtmp/COPYING COPYING.librtmp oeg_include_files COPYING* README
oegimport openssl # required, needs zlib
XLDFLAGS="-L${OEG_BASE_LIB_DIR}" make SYS=mingw CRYPTO=OPENSSL prefix=${OEG_BASE_DIR} libdir=${OEG_BASE_LIB_DIR} bindir=${OEG_BASE_BIN_DIR} XLDFLAGS="-L${OEG_BASE_LIB_DIR}" make install SYS=mingw CRYPTO=OPENSSL prefix=${OEG_BASE_DIR} libdir=${OEG_BASE_LIB_DIR} bindir=${OEG_BASE_BIN_DIR} DESTDIR=${OEG_INSTALL_DIR} # ${CONFIGURE_HOST_PARA}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" mv -i sbin/* bin${OEG_TARGETBITS_DIR_EXT}/ rm -rf sbin # Windows doesn't know anything about sbin directories. # no .la files oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
