set.commands:
PACKAGE_NAME=rtmpdump PACKAGE_VERSION=2.3
oegextract ${PACKAGE_NAME}-${PACKAGE_VERSION}.tar.gz
oegimport openssl oegimport zlib
mkdir -p "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}" cp librtmp/COPYING COPYING.librtmp cp COPYING* README "${OEG_INSTALL_DIR}${OEG_BASE_DIR}/packages/${PACKAGE_NAME}-${PACKAGE_VERSION}/"
XLDFLAGS="-L${OEG_BASE_LIB_DIR}" make SYS=mingw CRYPTO=OPENSSL prefix=/open-egovernment-install${OEG_BASE_DIR} libdir=${OEG_BASE_LIB_DIR} bindir=${OEG_BASE_BIN_DIR} XLDFLAGS="-L${OEG_BASE_LIB_DIR}" make install SYS=mingw CRYPTO=OPENSSL prefix=/open-egovernment-install${OEG_BASE_DIR} libdir=${OEG_BASE_LIB_DIR} bindir=${OEG_BASE_BIN_DIR} DESTDIR=${OEG_INSTALL_DIR}
# ${CONFIGURE_HOST_PARA}
cd "${OEG_INSTALL_DIR}${OEG_BASE_DIR}" mv ../open-egovernment-install/open-egovernment/* . oegarchive ${PACKAGE_NAME}-${PACKAGE_VERSION}
